import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './hooks/useAuth';
import LoadingSpinner from './components/common/LoadingSpinner';
import ProtectedRoute from './components/common/ProtectedRoute';
import Layout from './components/layout/Layout';

// Import components directly to avoid chunk loading issues
import Login from './pages/auth/Login';
import Register from './pages/auth/Register';
import Dashboard from './pages/Dashboard';
import NotFound from './pages/NotFound';

// Import expense components
import Expenses from './pages/Expenses';
import ExpenseForm from './pages/ExpenseForm';
import EnhancedExpenseForm from './pages/EnhancedExpenseForm';
import ExpenseDetail from './pages/ExpenseDetail';
import OfficeSuppliesExpense from './pages/OfficeSuppliesExpense';

// Import placeholder components
import { 
  Profile, 
  Approvals, 
  Users, 
  Company, 
  Workflows, 
  Analytics,
  Reports,
  Settings 
} from './pages/PlaceholderPages';

// Import advanced components
import ApprovalWorkflow from './components/approval/ApprovalWorkflow';
import FinancialControls from './components/financial/FinancialControls';
import AdvancedReceiptManager from './components/receipt/AdvancedReceiptManager';
import AdvancedReporting from './components/reporting/AdvancedReporting';
import IntegrationAutomation from './components/automation/IntegrationAutomation';

// Import landing page
import LandingPage from './pages/LandingPage';
import PricingPage from './pages/PricingPage';

function App() {
  const { user, isLoading } = useAuth();

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-[#ffffff]">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  return (
    <div className="App">
      <Routes>
          {/* Landing page - always shows first for non-authenticated users */}
          <Route
            path="/"
            element={!user ? <LandingPage /> : <Navigate to="/dashboard" replace />}
          />
          
          {/* Public routes */}
          <Route
            path="/pricing"
            element={!user ? <PricingPage /> : <Navigate to="/dashboard" replace />}
          />
          <Route
            path="/login"
            element={!user ? <Login /> : <Navigate to="/dashboard" replace />}
          />
          <Route
            path="/register"
            element={!user ? <Register /> : <Navigate to="/dashboard" replace />}
          />

          {/* Protected routes */}
          <Route
            path="/"
            element={
              <ProtectedRoute>
                <Layout />
              </ProtectedRoute>
            }
          >
            <Route index element={<Navigate to="/dashboard" replace />} />
            <Route path="dashboard" element={<Dashboard />} />
            <Route path="reports" element={<Reports />} />
            <Route path="settings" element={<Settings />} />
            <Route path="profile" element={<Profile />} />
            
            {/* Expense routes */}
            <Route path="expenses" element={<Expenses />} />
            <Route path="expenses/new" element={<ExpenseForm />} />
            <Route path="expenses/enhanced" element={<EnhancedExpenseForm />} />
            <Route path="expenses/:id" element={<ExpenseDetail />} />
            <Route path="expenses/:id/edit" element={<ExpenseForm />} />
            <Route path="expenses/office-supplies" element={<OfficeSuppliesExpense />} />

            {/* Approval routes (Manager/Admin only) */}
            <Route
              path="approvals"
              element={
                <ProtectedRoute allowedRoles={['manager', 'admin']}>
                  <Approvals />
                </ProtectedRoute>
              }
            />

            {/* Admin routes */}
            <Route
              path="admin/users"
              element={
                <ProtectedRoute allowedRoles={['admin']}>
                  <Users />
                </ProtectedRoute>
              }
            />
            <Route
              path="admin/company"
              element={
                <ProtectedRoute allowedRoles={['admin']}>
                  <Company />
                </ProtectedRoute>
              }
            />
            <Route
              path="admin/workflows"
              element={
                <ProtectedRoute allowedRoles={['admin']}>
                  <Workflows />
                </ProtectedRoute>
              }
            />

            {/* Analytics routes (Admin/Manager only) */}
            <Route
              path="analytics"
              element={
                <ProtectedRoute allowedRoles={['admin', 'manager']}>
                  <Analytics />
                </ProtectedRoute>
              }
            />

            {/* Advanced Features */}
            <Route
              path="advanced/approvals"
              element={
                <ProtectedRoute allowedRoles={['admin', 'manager']}>
                  <ApprovalWorkflow />
                </ProtectedRoute>
              }
            />
            <Route
              path="advanced/financial-controls"
              element={
                <ProtectedRoute allowedRoles={['admin', 'manager']}>
                  <FinancialControls />
                </ProtectedRoute>
              }
            />
            <Route
              path="advanced/receipt-manager"
              element={
                <AdvancedReceiptManager />
              }
            />
            <Route
              path="advanced/reporting"
              element={
                <ProtectedRoute allowedRoles={['admin', 'manager']}>
                  <AdvancedReporting />
                </ProtectedRoute>
              }
            />
            <Route
              path="advanced/integration"
              element={
                <ProtectedRoute allowedRoles={['admin']}>
                  <IntegrationAutomation />
                </ProtectedRoute>
              }
            />
          </Route>

          {/* 404 route */}
          <Route path="*" element={<NotFound />} />
        </Routes>
    </div>
  );
}

export default App;