import React from 'react';
import { NavLink } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';

const Sidebar = () => {
  const { user } = useAuth();

  const navigation = [
    { name: 'Dashboard', href: '/dashboard', icon: 'üìä' },
    { name: 'Expenses', href: '/expenses', icon: 'üí∞' },
    ...(user?.role === 'manager' || user?.role === 'admin' 
      ? [{ name: 'Approvals', href: '/approvals', icon: '‚úÖ' }] 
      : []
    ),
    ...(user?.role === 'admin' 
      ? [
          { name: 'Users', href: '/admin/users', icon: 'üë•' },
          { name: 'Company', href: '/admin/company', icon: 'üè¢' },
          { name: 'Workflows', href: '/admin/workflows', icon: '‚öôÔ∏è' },
        ] 
      : []
    ),
    ...(user?.role === 'admin' || user?.role === 'manager' 
      ? [{ name: 'Analytics', href: '/analytics', icon: 'üìà' }] 
      : []
    ),
    { name: 'Profile', href: '/profile', icon: 'üë§' },
  ];

  return (
    <div className="bg-white shadow-sm border-r border-gray-200 w-64 min-h-screen">
      <div className="p-4">
        <nav className="space-y-2">
          {navigation.map((item) => (
            <NavLink
              key={item.name}
              to={item.href}
              className={({ isActive }) =>
                `flex items-center px-4 py-2 text-sm font-medium rounded-md transition-colors ${
                  isActive
                    ? 'bg-blue-50 text-blue-700 border-r-2 border-blue-600'
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                }`
              }
            >
              <span className="mr-3">{item.icon}</span>
              {item.name}
            </NavLink>
          ))}
        </nav>
      </div>
    </div>
  );
};

export default Sidebar;
